class ListItem extends Component{
    constructor(props){
        super(props)
    }

    onChangedone = () =>{
        if(this.props.item.done){
            this.props.item.done = false;
        }
    }

    render() {
        if(this.props.item.done) {
            return(
                <div className = "done-item">{this.props.item.content}</div>
            )
        }else{
            return <p className = "item">{this.props.item.content}</p>
        }
    }
}

export default ListItem;
