class ListItem extends Component{
    constructor(props){
        super(props)
    }

    onChangedone = () =>{
        this.getDom.className = class1
        if(this.props.item.done){
            this.props.item.done = false;
        }else{
            this.props.item.done = true;
        }
        alert(class1)
    }

    render() {
        if(this.props.item.done) {
            return(
                <div className = "done-item">
                    {this.props.item.content}
                    <button onClick={this.onChangedone}>Change</button>  
                </div>
            )
        }else{
            return(
                <div className = "item">
                    {this.props.item.content}
                    <button onClick={this.onChangedone} >Change</button>     
                </div>
            ) 
        }
    }
}

export default ListItem;
