import React, { Component } from 'react';
import './ListItem.css'



// const ListItem = (props) => {
//     const item = props.item;

//     if(item.done) {
//         return <p onClick={this.onChangedone} className = "done-item">{item.content}</p>
//     }else{
//         return <p className = "item">{item.content}</p>
//     }
// }


class ListItem extends Component{
    constructor(props){
        super(props)
    }

    onChangedone = () =>{
        // const class1 = this.getDom.className
        if(this.props.item.done === "true"){
            this.props.item.done = "false";
        }
        if(this.props.item.done === "false"){
            this.props.item.done = "true";
        }
        alert(this.props.item.done)
    }

    render() {
        if(this.props.item.done === "true") {
            return(
                <div className = {this.props.item.done}>
                    {this.props.item.content}
                    <button onClick={this.onChangedone}>Change</button>  
                </div>
            )
        }else{
            return(
                <div className = {this.props.item.done}>
                    {this.props.item.content}
                    <button onClick={this.onChangedone}>Change</button>     
                </div>
            ) 
        }
    }
}

export default ListItem;
