class ListItem extends Component{
    constructor(props){
        super(props);
        this.state = {
            True: "true",
            False: "false"
        }
    }

    onChangedone = () =>{
        // const class1 = this.getDom.className
        if(this.props.item.done === "true"){
            this.props.item.done = "false";
        }else{
            this.props.item.done = "true";
        }

        // this.props.addItem(this.state.inputContent)
        // this.setState({
        //     inputContent: ''
        // })
        // this.setState({
        //     inputContent: event.target.value
        // })
        alert(this.props.item.done)
    }

    render() {
        if(this.props.item.done === "true") {
            return(
                <div className = {this.state.True}>
                    {this.props.item.content}
                    <button onClick={this.onChangedone}>Change</button>  
                </div>
            )
        }else{
            return(
                <div className = {this.state.False}>
                    {this.props.item.content}
                    <button onClick={this.onChangedone}>Change</button>     
                </div>
            ) 
        }
    }
}

export default ListItem;
