import React, { Component } from 'react';
import './ListItem.css';
import { Modal} from 'react';
const confirm = Modal.confirm;

// const ListItem = (props) => {
//     const item = props.item;

//     if(item.done) {
//         return <p onClick={this.onChangedone} className = "done-item">{item.content}</p>
//     }else{
//         return <p className = "item">{item.content}</p>
//     }
// }


class ListItem extends Component{
    constructor(props){
        super(props);
        this.state = {
            situation: "",
            deleted: ""
        }
        this.state.situation = this.props.item.done
    }

    onChangeDone = () =>{
        if(this.state.situation === "false"){
            this.setState({
                situation:"true"
            })
        }else{
            alert("This task has already finished")
        }
        // alert(this.state.situation)
    }

    onChangeUndo = () =>{
        if(this.state.situation === "true"){
            this.setState({
                situation:"false"
            })
        }else{
            alert("This task is unfinished")
        }
    }

    onChangeDelete = () =>{
        this.setState({
            deleted: this.props.item
        })
        confirm({
            title: 'Do you want to delete the tasks?',
            onOk() {
                this.props.deleteItem(this.state.deleted)
            }
          });

    }

    render() {
        if(this.props.item.done === "true") {
            return(
                <div className = {this.state.situation} key = {this.props.item.content}>
                    {this.props.item.content}
                    <button onClick={this.onChangeDone}>Finish</button>
                    <button onClick={this.onChangeUndo}>Unfinish</button> 
                    <button onClick={this.onChangeDelete}>Delete</button> 

                </div>
            )
        }else{
            return(
                <div className = {this.state.situation} key = {this.props.item.content}>
                    {this.props.item.content}
                    <button onClick={this.onChangeDone}>Finish</button>     
                    <button onClick={this.onChangeUndo}>Unfinish</button> 
                    <button onClick={this.onChangeDelete}>Delete</button>
                </div>
            ) 
        }
    }
}

export default ListItem;
